import { configure } from '@storybook/react';
import { addParameters } from '@storybook/react';
import mdx from '../src/js/components/button.stories.mdx';
import { DocsContainer } from '@storybook/addon-docs/blocks';
// import { mdx } from '@mdx-js/react';

const loaderFn = () => {
  // manual loading
  require('../src/js/components/button.component.js');
  // require('./button.stories.js');

  // dynamic loading, unavailable in react-native
  const req = require.context('../src/js/components', true, /\.stories\.js$/);
  req.keys().forEach(fname => req(fname));
};


configure(loaderFn, module);
// configure(require.context('../src/js/components', true, /\.stories\.js$/), module);
addParameters({
    docs: {
      container: DocsContainer,
      page: mdx,
    },
  });
