import React from 'react';
import { action } from '@storybook/addon-actions';
import Button from '@material-ui/core/Button';
import { storiesOf } from '@storybook/react';
import '@storybook/addon-actions/register';
import { withKnobs,boolean,text } from '@storybook/addon-knobs';
import mdx from './button.stories.mdx';




//**********canvas,notes,addons-action ***********************/
export default {
  title: 'Button',
  component: Button,
  componentSubtitle: 'Displays a different types of button',

  parameters: {
    docs: {
      page: mdx,
      storyDescription: '5 different types of button.'
    },
  },
  decorators: [withKnobs],
  // Our exports that end in "Data" are not stories.
  excludeStories: /.*Data$/,
};


storiesOf('Button', module)
  .addDecorator(withKnobs)
  .addParameters({
    backgrounds: [
      { name: 'twitter', value: '#00aced', default: false },
      { name: 'facebook', value: '#3b5998' },
      { name: 'default', value:'#0000' }
    ],
  })
  .add('default', () => <Button onClick={action('buttonClicked')}
                                size={text('size','large')}>
                              {text('Label','Hello, Storybook!')}
                        </Button>)
  .add('primary', () => <Button onClick={action('buttonClicked')} 
                                color={text('color','secondary')} 
                                variant={text('variant','contained')}
                                size={text('size','medium')}>
                                {text('title','Primary')}
                        </Button>)
  .add('secondary', () => <Button onClick={action('buttonClicked')} 
                                  color={text('color','secondary')} 
                                  variant={text('variant','contained')}
                                  size={text('size','small')}>
                                  {text('title','Secondary')}
                          </Button>)
  .add('disabled', () => <Button onClick={action('buttonClicked')} 
                                disabled={boolean('Disabled',true)} 
                                variant={text('variant','contained')}
                                size={text('size','large')}>
                                {text('title','Disabled')}
                        </Button>)
  .add('link', () => <Button onClick={action('buttonClicked')} 
                            color={text('color','primary')} 
                            variant={text('variant','contained')} 
                            href="#contained-buttons"
                            size={text('size','medium')}>
                            {text('title','Link')}
                      </Button>);







