import { configure } from '@storybook/react';
import { addParameters,addDecorator } from '@storybook/react';
import { DocsPage,DocsContainer } from '@storybook/addon-docs/blocks';
import { themes } from '@storybook/theming';
import { withInfo } from '@storybook/addon-info';
import Button from '../src/js/components/button.component';
// import backgrounds from 'react-storybook-addon-backgrounds';
import mdx from '../src/js/components/button.component.mdx';
import { withKnobs } from '@storybook/addon-knobs';
// import { DocsPage,DocsContainer } from '@storybook/addon-docs/blocks';
import Theme from './theme';
import { withStyles } from 'storybook-addon-styles/react';

configure(require.context('../src/js/components', true, /\.stories\.js$/), module);
addDecorator(withKnobs);
addDecorator(
  withInfo({
    header: true,
    source:true,
    // excludedPropTypes:[(<Button />)]
  })
);
addDecorator(withStyles({
  fontFamily: "'Roboto', Helvetica, sans-serif",
  padding: '40px'
}));

// ...and/or with parameters
addParameters({
  styles: {
    border: '1px solid black'
  }
})

addParameters({
    component:Button,
    options:{
      theme:Theme
    },
    // componentSubtitle: 'Documentation of the Button React component',  
    // info: {
      // disable:true,
      // defaultProps:{disable:false},
      // inline: true,
      text:"Button is a default component to display action in a page.",
      // excludedPropTypes:[<Button />]
    // },
    docs: {
      // page: mdx,
      page:DocsPage,
    },
  });
