/* eslint-disable */
// @ts-nocheck
// @ts-ignore
var withSourceLoader = require('@storybook/source-loader/preview').withSource;
// @ts-ignore
var addSourceDecorator = require("@storybook/source-loader/preview").addSource;
// @ts-ignore
var __SOURCE_PREFIX__ = "/home/mtech/priyal/projects/buttoncomponent/src/js/components";
// @ts-ignore
var __STORY__ = "import React from 'react';
import { action } from '@storybook/addon-actions';
import Button from './button.component';
import { withKnobs, text, boolean,select,optionsKnob as option } from "@storybook/addon-knobs";


export default {
  title: 'Button',
  decorators: [withKnobs],
  excludeStories: /.*Data$/,
  includedStories: [],
  component:Button  
};

const label='Type';
const options = {
  Long:'long',
  Short:'short',
  Alert:'alert',
  Outlined:'outlined',
  Link:'link',
  Delete:'delete',
  Line:'line',
  Icon:'icon'
}

const defaultValue='short';
// const groupId='GROUPT-ID1';
const value=select(label,options,defaultValue);
// button(label,options,defaultValue);

export const Short = () => (<Button  disabled={disable} type={value}
                                          handleClick={action('Short button clicked')}
                                          handleInputFocus={action('onFocus')}
                                          handleInputBlur={action('onBlur')}
                                          btnText={text("label","Short")}
                                          variant={text("variant","contained")}
                                          disableRipple={boolean("disableRipple", true)} ></Button>
                                          );

export const Long = () => (<Button disabled={boolean("Disabled", false)} variant={text("variant","contained")} fullWidth={boolean("fullWidth", true)} disableRipple={boolean("disableRipple", false)} type={text("Type", 'long')} handleClick={action('onClick')} handleInputFocus={action('onFocus')}  handleInputBlur={action('onBlur')} btnText={text("label",'Long')}/>);



export const Alert = () => (<Button 
                            disabled={boolean("Disabled", false)}
                            type={text("Type", "alert")}
                            handleClick={action('Alert button clicked')}
                            handleInputFocus={action('onFocus')}
                            handleInputBlur={action('onBlur')}
                            btnText={text("label","Alert")}
                            fullWidth={boolean("fullWidth", false)}
                            variant={text("variant","outlined")}
                            disableRipple={boolean("disableRipple", true)}>
                        </Button>);

export const outlined = () => (<Button 
                            disabled={boolean("Disabled", false)}
                            type={text("Type", "outlined")}
                            handleClick={action('outlined button clicked')}
                            handleInputFocus={action('onFocus')}
                            handleInputBlur={action('onBlur')}
                            btnText={text("label","Outlined")}
                            fullWidth={boolean("fullWidth", false)}
                            variant={text("variant","outlined")}
                            disableRipple={boolean("disableRipple", true)}>
                        </Button>);

export const Link = () => (<Button 
                            disabled={boolean("Disabled", false)}
                            type={text("Type", "link")}
                            handleClick={action('Link button clicked')}
                            handleInputFocus={action('onFocus')}
