import {
  configure
} from '@storybook/react';
import {
  addParameters,
  addDecorator
} from '@storybook/react';
import {
  DocsPage,
  DocsContainer
} from '@storybook/addon-docs/blocks';
import {
  withInfo
} from '@storybook/addon-info';
import Button from '../src/js/components/button.component';
import mdx from '../src/js/components/button.component.mdx';
import {
  withKnobs
} from '@storybook/addon-knobs';
import Theme from './theme';
import {
  withStyles
} from 'storybook-addon-styles/react';

configure(require.context('../src/js/components', true, /\.stories\.js$/), module);
addDecorator(withKnobs);
addDecorator(
  withInfo({
    header: true,
    source: true,
  })
);
addDecorator(withStyles({
  fontFamily: "'Roboto', Helvetica, sans-serif",
  padding: '40px',
  background: '#FF69B4',
  align: 'center'
}));

addParameters({
  styles: {
    border: '1px solid black',
    background: '#FF69B4',
  }
});

addParameters({
  component: Button,
  options: {
    theme: Theme
  },
  text: "Button is a default component to display action in a page.",
  docs: {
    page: DocsPage,
  },
});
